import math

def f(x, a=1.0, b=0.1):
    # Parametric Gaussian family with a cosine modulation
    return math.exp(-a*x*x) * (1 + b*math.cos(x))

def search_algorithm():
    # For this problem, return f itself.
    # OpenEvolve will mutate the f() body, not this function.
    return f
