import numpy as np

def f(x):
    # Lorentzian with a tunable width parameter
    a = 1.0  # Width parameter. The optimizer will adjust this.
    return (1/np.pi) * (a / (a**2 + x**2))

def search_algorithm():
    # For this problem, return f itself.
    # OpenEvolve will mutate the f() body, not this function.
    return f
