import math

def f(x, a=1.0):
    # Gaussian function, parameterized by 'a' (standard deviation)
    return (1 / (a * math.sqrt(2*math.pi))) * math.exp(-0.5 * (x / a)**2)

def search_algorithm():
    # For this problem, return f itself.
    # OpenEvolve will mutate the f() body, not this function.
    return f
