import numpy as np

def f(x):
    # Gaussian function with tunable width.
    a = 1.0  # Width parameter (can be optimized by the evolutionary search).
    return (1 / np.sqrt(a * np.pi)) * np.exp(-(x*x)/a)

def search_algorithm():
    # For this problem, return f itself.
    # OpenEvolve will mutate the f() body, not this function.
    return f
